import { Card } from "antd";
import {RetweetOutlined, HeartOutlined, MessageOutlined, Popover, EllipsisOutlined} from"@ant-design";

const PostCard = ({post}) =>{
    return(
        <div>
            <Card 
                cover ={post.Images[0] && <PostImages Images={[post.Images]}/>}
                actions={[
                    <RetweetOutlined key="retweet"/>,
                    <HeartOutlined key="heart"/>,
                    <MessageOutlined key="comment"/>,
                    <Popover key="more">
                        <EllipsisOutlined/>
                    </Popover>,
                ]}
            >
                <Image/>
                <Content/>
                <Buttons></Buttons>
            </Card>
            <CommentForm/>
            <Comments/>
        </div>
    )
};

export default PostCard;
