import { Card } from "antd";
import {RetweetOutlinde} from"@ant-design/icons/lib/icons/RetweetOutlined";

const PostCard = ({post}) =>{
    return(
        <div>
            <Card 
                cover ={post.Images[0] && <PostImages Images={[post.Images]}/>}
                actions={[
                    <RetweetOutlinde/>
                ]}
            >
                <Image/>
                <Content/>
                <Buttons></Buttons>
            </Card>
            <CommentForm/>
            <Comments/>
        </div>
    )
};

export default PostCard;
