import { Card } from "antd";
import {RetweetOutlined, HeartOutlined, MessageOutlined, Popover} from"@ant-design";

const PostCard = ({post}) =>{
    return(
        <div>
            <Card 
                cover ={post.Images[0] && <PostImages Images={[post.Images]}/>}
                actions={[
                    <RetweetOutlined/>,
                    <HeartOutlined/>,
                    <MessageOutlined/>,
                    <Popover/>,
                ]}
            >
                <Image/>
                <Content/>
                <Buttons></Buttons>
            </Card>
            <CommentForm/>
            <Comments/>
        </div>
    )
};

export default PostCard;
