import {Button, Form, Input} from "antd";
import { useCallback, useState } from 'react';
import Link from "next/link";

const Loginform = () =>{
    const [id, setId] = useState("");
    const [password, setPassword] = useState("");

    const onChangeId = useCallback((e) =>{
        setId(e.target.value);
    }, []);

    const onChangePassword = useCallback((e) =>{
        setPassword(e.target.value);
    }, []);

    return(
        <>
            <Form>
                <div>
                    <label htmlFor="user-id">아이디</label>
                    <br/>
                    <Input name="user-id" value={id} onCange={onChangeId} required/>
                </div>
                <div>
                    <label htmlFor="user-password">아이디</label>
                    <br/>
                    <Input name="user-password" type="password" value={password} onCange={onChangePassword} required/>
                </div>
                <div>
                    <Button type="primary" htmlType="submit" loading={false}></Button>
                    <Link href="/signup"><a><Button>회원가입</Button></a></Link>
                </div>
            </Form>
        </>
    )
}
export default Loginform;
