const initialState = {
    user :{
        isLoggedIn : false,
        user : null,
        signUpData : {},
        loginData : {},
    },
    post : {
        maingPosts : [],
    }
};

const login =(data) => {
    return{
        type : "LOG_IN",
        data,
    }
};

const rootReducer = ((state = initialState, action) =>{
    switch(action.type){
        case "LOG_IN":
            return{
                ...state,
                name : action.data,
            }
    }
});

export default rootReducer;
