import {List, Button, Card} from "antd";
import { PropTypes } from 'prop-types';
import {StopOutlined} from "@ant-design/icons";
import { useDispatch } from 'react-redux';
import { UNFOLLOW_REQUEST, REMOVE_FOLLOWERS_REQUEST } from "../reducers/user";

const FollowList = ({header, data}) =>{

    const dispatch = useDispatch();

    const onCancel =(id) => () =>{
        if(header === "팔로잉"){
            dispatch({
                type : UNFOLLOW_REQUEST,
                data : id,
            });
        }
        dispatch({
            type : REMOVE_FOLLOWERS_REQUEST,
            data : id,
        });
    };
    return(
        <List
            style={{ marginBottom : 20}}
            grid={{ gutter: 4, xs: 2, sm: 3, md: 3, lg: 3, xl: 3, xxl:3}}
            size="small"
            header={<div>{header}</div>}
            loadMore={<div style={{textAlign:"center", margin:"10px 0"}}><Button>더 보기</Button></div>}
            bordered
            dataSource={data}
            renderItem={(item) =>(
                <List.Item style={{marginTop : 20}}>
                    <Card actions={[<StopOutlined onClick={onCancel(item.id)} key="stop"/>]}>
                        <Card.Meta description={item.nickname}/>
                    </Card>
                </List.Item>
            )}
        />
    );
};

FollowList.prototype = {
    header : PropTypes.string.isRequired,
    data : PropTypes.array.isRequired,
};

export default FollowList;
